import { shopActionTypes } from "./ShopActionTypes";


export const shopUpdate = () => ({
    type : shopActionTypes.ON_ITEMS_UPDATE
});

export const shopUpdateStart = () => ({
    type : shopActionTypes.FETCH_ITEMS_START
});

export const shopUpdateSuccess = () => ({
    type : shopActionTypes.FETCH_ITEMS_SUCCESS
});

export const shopUpdateFail = (data) => ({
    type : shopActionTypes.FETCH_ITEMS_FAIL,
    error : data
});
