import { userActionTypes } from "./UserAcionTypes";
import { firestore, auth, provider, createUserProfileDocument } from "../../firebase/firebase.utils";
import { put, takeEvery , takeLatest } from 'redux-saga/effects';

export const setCurrentUser = user => (
    {
        type : userActionTypes.SET_CURRENT_USER,
        payload : user
});

export function* GoogleStartSignIn()
{
    const userRef = yield auth.signInWithPopup(provider);
}

export function* UserSagas()
{
    yield takeLatest(userActionTypes.GOOGLE_START_SIGNIN, GoogleStartSignIn);
};
