import { userActionTypes } from "./UserAcionTypes";
import { firestore, auth, provider, createUserProfileDocument } from "../../firebase/firebase.utils";
import { put, takeEvery , takeLatest, all ,call } from 'redux-saga/effects';

export function* onGoogleStartSignIn()
{
   const userAuth = yield auth.signInWithPopup(provider);
    
    try {
        const userRef = yield createUserProfileDocument(userAuth, '');
        const userSnapshot = yield userRef.get();
        console.log(userSnapshot);
    }catch(err)
    {
        
    }
};

export function* GoogleStartSignIn()
{
   yield takeLatest(userActionTypes.GOOGLE_START_SIGNIN, onGoogleStartSignIn);
};

export function* userSagas()
{
    yield all([call(GoogleStartSignIn)]);
};
