import { userActionTypes } from "./UserAcionTypes";
import { firestore, auth, provider, createUserProfileDocument } from "../../firebase/firebase.utils";
import { put, takeEvery , takeLatest, all ,call } from 'redux-saga/effects';

export const setCurrentUser = user => (
{
    type : userActionTypes.SET_CURRENT_USER,
    payload : user
});


export const GoogleStartSignIn = user => (
{
    type : userActionTypes.GOOGLE_START_SIGNIN
});

export const GoogleSignInSuccess = data => (
{
    type : userActionTypes.GOOGLE_START_SIGNIN_SUCCESS,
    payload : data
});

export const GoogleFailSignIn = user => (
{
        type : userActionTypes.GOOGLE_START_SIGNIN
});
