import React from 'react';
import { Route, withRouter } from 'react-router';
import { connect } from 'react-redux';
import CollectionsOverview from '../../Components/CollectionsOverview/CollectionsOverview.component';
import CollectionsPage from '../Collection/CollectionsPage.component';
import ItemPage from '../Item/ItemPage.component';
import { firestore, convertCollectionsSnapshotToMap } from '../../firebase/firebase.utils';
import { updateCollections } from '../../Redux/Shop/shop.actions';
import WithSpinner from '../../Components/WithSpinner/WithSpinner.component';

const CollectionsOverviewSpinner = WithSpinner(CollectionsOverview);
const CollectionsPageSpinner = WithSpinner(CollectionsPage);
const ItemPageSpinner = WithSpinner(ItemPage);


const  Shop = ({match}) =>  {
   



    const { loading } = this.props.isFetching;

    return(
            <div>
                <Route exact path={`${match.path}`} render = { (props) => <CollectionsOverviewSpinner  isLoading={loading} {...props} />}/>
                <Route exact  path = {`${match.path}/:collectionId`}  render = { (props) => <CollectionsPageSpinner  isLoading={loading} {...props} />}/>
                <Route  path = {`${match.path}/:collectionId/:itemId`} render={ (props) => <ItemPageSpinner isLoading={loading} {...props}/>}/> 
        </div>
    );    
}

const mapDispatchToProps = dispatch => ({
    updateCollections : collectionsMap => dispatch(updateCollections(collectionsMap))
});

export default withRouter(connect(null,mapDispatchToProps)(Shop));
