import styled from "styled-components";
import Button from "../Button/Button.component";

export const ViewItemButton = styled(Button)`
    width: 80%;
    opacity: 0.7;
    position: absolute;
    top: 255px;
    display: none;

    &:hover {
        opacity: 0.85;
        display: flex;
    }
`;


export const CollectionItemContainer = styled.div`
    width: 22vw;
    display: flex;
    flex-direction: column;
    height: 350px;
    align-items: center;
    position: relative;
`;

export const CollectionImage = ({imageUrl}) => styled.image`

`;
