import React, { useEffect, useState } from 'react';
import CollectionItem from '../CollectionItem/CollectionItem.component';
import { useSelector } from 'react-redux';
import { withRouter, useHistory } from 'react-router-dom'; 
import {selectCollectionItems} from '../../Redux/Shop/shop.selector';
import { ReactComponent as LeftArrowLogo} from '../../Assets/leftArrow.svg';
import { ReactComponent as RightArrowLogo} from '../../Assets/rightArrow.svg';
import { CollectionPreviewContainer, TitleContainer , PreviewContainer, LeftArrowContainer, RightArrowContainer} from './Collection-Preview.styles';

export  { LeftArrowLogo, RightArrowLogo };

const ColllectionPreview = ({collectionCategory}) => 
{
    const [state,setState] = useState = ({
        startIndex : 0,
        endIndex : 3,
        maxItems : shopItems.items? shopItems.items.length -1 : 0,
        previewCount  : 4,
        title : null
    });

    const history = useHistory();
    const shopItems = useSelector((state) => selectCollectionItems(collectionCategory.title)(state));
    
    useEffect(() => {
        setState({...state, maxItems : shopItems.items ? shopItems.items.length -1 : 0, title : collectionCategory} )
    },[collectionCategory]);

    /*
    constructor(props)
    {
        super(props);
        this.state = {
            startIndex : 0,
            endIndex : 3,
            maxItems :  props.shopItems.items?  props.shopItems.items.length -1 : 0,
            previewCount  : 4,
            title : this.props.collectionCategory.title
        };
    }*/

    const onArrowClick = (direction) => {
        //max amount of items to preview on screen per category
        const previewCount = state.previewCount;
        //caluclate offset from array boundary to closest index based on direction
        const diff = direction  === 'right' ? (state.maxItems - state.endIndex) :  ((0 - state.startIndex) * -1);
        //if at array boundary do nothing
        if(diff === 0)
            return null;
            //if array boundary is closer than the max amount 0of items we can display on screen
            //move furthest index to render newest item
            //move closest index to render the last item
            diff <= previewCount ?
                direction === 'left' ?  setState({ endIndex : state.startIndex - 1, startIndex : state.startIndex - diff}) :
                setState({ startIndex : state.endIndex + 1, endIndex : state.endIndex + diff})
            :
                direction === 'left' ?  setState({ endIndex : state.startIndex - 1, startIndex : state.startIndex - previewCount}) :
                setState({ startIndex : state.endIndex + 1, endIndex : state.endIndex + previewCount})

    };

  
    return(
        <CollectionPreviewContainer> 
            <TitleContainer  onClick={ () => history.push(`${this.props.match.url}/${state.title}`)}> {state.title.toUpperCase()}</TitleContainer>
            <PreviewContainer>
                <LeftArrowContainer onClick={() => onArrowClick('left')}/>
                {                                          //render only the indexs we have chosen max of 4
                    shopItems.items.filter((item, idx) => (idx >= state.startIndex && idx <= state.endIndex) )
                    .map((item) => (
                        <CollectionItem key={item.id} {...item} /> 
                    ))
                }
                <RightArrowContainer onClick={() => onArrowClick('right')}/>
            </PreviewContainer>
        </CollectionPreviewContainer>
    );
};



export default withRouter(ColllectionPreview);


