import { userActionTypes } from "./UserAcionTypes";
import { firestore, auth, provider, createUserProfileDocument } from "../../firebase/firebase.utils";
import { put, takeEvery , takeLatest, all ,call } from 'redux-saga/effects';
import { GoogleSignInSuccess, GoogleSignInFail, EmailSignInSuccess } from "./user-actions";
export function* onGoogleStartSignIn()
{
   const userAuth = yield auth.signInWithPopup(provider);
    if(userAuth)
    {
        try {
            const userRef = yield createUserProfileDocument(userAuth.user, '');
            const userSnapshot = yield userRef.get();
            yield put(GoogleSignInSuccess({id : userSnapshot.id, ...userSnapshot.data()}));
        }catch(err)
        {
            yield put(GoogleSignInFail({ error: err}));
        }
    }
};

export function* GoogleStartSignIn()
{
   yield takeLatest(userActionTypes.GOOGLE_START_SIGNIN, onGoogleStartSignIn);
};

export function* onEmailStartSignIn(data)
{
  
    try {
        const userAuth = yield auth.signInWithEmailAndPassword(data.payload.email,data.payload.password);
         const userRef = yield createUserProfileDocument( userAuth.user, '');
        //yield put(EmailSignInSuccess())
    }
    catch ( err) {
        console.log(err);
    }
   
};

export function* EmailStartSignIn()
{
   yield takeLatest(userActionTypes.EMAIL_START_SIGNIN, onEmailStartSignIn);
};

export function* userSagas()
{
    yield all([call(GoogleStartSignIn), call(EmailStartSignIn)]);
};
