import React from 'react';
import { Route, withRouter } from 'react-router';
import { connect } from 'react-redux';
import CollectionsOverview from '../../Components/CollectionsOverview/CollectionsOverview.component';
import CollectionsPage from '../Collection/CollectionsPage.component';
import ItemPage from '../Item/ItemPage.component';
import { getShoppingData } from '../../firebase/firebase.utils';
import { firestore } from '../../firebase/firebase.utils';
class Shop extends React.Component {

    constructor(props)
    {
        super(props);
        this.state = {
            match : props.match,
            shoppingData : null
        };  
    }

    componentDidMount()
    {
        const collectionRef =  firestore.collection('Collections');
      
        collectionRef.onSnapshot(async snapshot => {
            console.log(snapshot.docs.data);
        });
       
    }

    render()
    {
        return(
            <div>
                <Route exact path={`${this.state.match.path}`} component={CollectionsOverview}/>
                <Route exact  path = {`${this.state.match.path}/:collectionId`} component={CollectionsPage}/>
                <Route  path = {`${this.state.match.path}/:collectionId/:itemId`} component={ItemPage}/>
        </div>
        );
    }    
}

const mapStateToProps = state => {
    return null;
}

const mapDispatchToProps = dispatch => {
    return null;
}

export default withRouter(connect(null,null)(Shop));
