import { ShopActionTypes } from "./shop.types";
import { firestore, convertCollectionsSnapshotToMap } from "../../firebase/firebase.utils";

export const updateCollections = (collectionsMap) => ({
    type :  ShopActionTypes.UPDATE_COLLECTION,
    payload : collectionsMap
});

export const updateCollectionsStart = () => ({
    id : ShopActionTypes.UPDATE_COLLECTION_START
});


export const updateCollectionSuccess = (data) => ({
        id: ShopActionTypes.UPDATE_COLLECTION_SUCCESS,
        payload : data
});

export const updateCollectionFailure = (data) => ({
    id: ShopActionTypes.UPDATE_COLLECTION_FAIL,
    error : data
});


export const updateCollectionAsync = () => {
    
    return (dispatch) => {

        const collectionRef = firestore.collection('Collections');
        dispatch(updateCollectionsStart());

        collectionRef.get().then(async (snapshot) =>{
            const collections = await convertCollectionsSnapshotToMap(snapshot);
            dispatch(updateCollectionSuccess(collections));
        }).catch(err => dispatch(updateCollectionFailure(err)));
    };
};
