import React from 'react';
import { connect } from 'react-redux';

import WithSpinner from '../../Components/WithSpinner/WithSpinner.component';

import { getIsFetching } from '../../Redux/Shop/shop.selector';
import { updateCollectionAsync } from '../../Redux/Shop/shop.actions';
import {  ShopActionTypes } from  '../../Redux/Shop/shop.types';
import { convertCollectionsSnapshotToMap } from '../../firebase/firebase.utils';
import { shopCollections } from '../../Redux/Shop/shop.selector';
import  ShoppingComponents  from '../../Components/ShoppingComponents/ShoppingComponents.component';


const ShoppingComponentsWithSpinner = WithSpinner(ShoppingComponents);

class Shop extends React.Component  {  

   componentDidMount()
   {
       console.log('SHOP MOUNTED');
       this.props.updateCollections();
   }
   
   render()
   {
       const { isFetching } = this.props;
       console.log(' Fetching Shop Items : ' , isFetching);
        return(
                <div>
                    <ShoppingComponentsWithSpinner isFetching={isFetching} {...this.props}/>
            </div>
        );
   }    
}

const mapStateToProps = (state) => ({
    isFetching : getIsFetching(state),
    shopItems :  shopCollections(state)
});

const mapDispatchToProps = dispatch => ({
    updateCollections : () => dispatch(updateCollectionAsync(ShopActionTypes,convertCollectionsSnapshotToMap))
});

export default connect(mapStateToProps,mapDispatchToProps)(Shop);
